    $(document).on('scroll', () => {
        if ($(".catalog--wrapper.top")[0].getBoundingClientRect().top < 112) {
            $(".catalog--wrapper.top").addClass('stick')
        } else {
            $(".catalog--wrapper.top").removeClass("stick");
        }
    });
    $(".catalog--wrapper__menu--title").on('click', () => {
        $(".catalog--wrapper.top.stick").toggleClass("showcatalog");
        if (!$(".catalog--wrapper.top.stick").hasClass(".showcatalog")) {
            $('.catalog--wrapper__filter.active').removeClass("active")
            $(".catalog--wrapper__menu--filter.active").removeClass('active');
        }
    });


    $(".catalog--wrapper__item_btn").each((index, item) => {
        $(item).on("click", () => {
            $(".modal--wrapper").addClass("active");
            $("body").css({ "overflow": "hidden" });
            return false;
        });
    });
    closeModal = () => {
        $(".modal--wrapper").removeClass("active");
        $("body").css({ "overflow": "auto" });
    }

    $(".mobile__menu_btn").on("click",()=>{
        $(".mobile__menu_btn").toggleClass("active");
        $(".mobile__menu_list").toggleClass("active");
        $("body").css({ "overflow": "hidden" });
        if(!$(".mobile__menu_list").hasClass("active")){
            $("body").css({ "overflow": "auto" });
        }
        return false;
    });
    $(".mobile__menu_cart").on("click",()=>{
        $(".catalog--wrapper__cart").toggleClass("active");
        $("body").css({ "overflow": "hidden" });
        if(!$(".catalog--wrapper__cart").hasClass("active")){
            $("body").css({ "overflow": "auto" });
        }
        return false;
    })
